import "./App.css";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom"; 
import Login from "./Components/login";
import Register from "./Components/Register";  
import { useEffect, useState } from "react";
import Navbar from "./Components/Navbar";
import TaskForm from "./Components/TaskForm";
import AdminTaskList from "./Components/AdminTaskList";
import ITStock from "./Components/ITStock";
import Jobs from "./Components/Jobs";
import React from 'react';
import Dashboard from "./Components/Dashboard"; 

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [role, setRole] = useState(null);

  useEffect(() => {
    const account = localStorage.getItem("account");
    const userRole = localStorage.getItem("role");

    setLoggedIn(!!account);  // Set to true if account exists
    setRole(userRole);
  }, []);

  useEffect(() => {
    // Update isLoggedIn and role if the account or role changes in localStorage
    const handleStorageChange = () => {
      const account = localStorage.getItem("account");
      const userRole = localStorage.getItem("role");
      setLoggedIn(!!account);
      setRole(userRole);
    };

    window.addEventListener("storage", handleStorageChange);
    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []);

  return (
    <div>
      <Router>
        {isLoggedIn && <Navbar />}  {/* Show Navbar only if logged in */}

        <Routes>
          {/* Public Route */}
          <Route
            path="/"
            element={
              isLoggedIn ? <Navigate to="/dashboard" /> : <Navigate to="/login" />
            }
          />

          {/* Registration Route */}
          <Route 
            path="/register" 
            element={isLoggedIn ? <Navigate to="/dashboard" /> : <Register />} 
          />

          {/* Login Route */}
          <Route 
            path="/login" 
            element={isLoggedIn ? <Navigate to="/dashboard" /> : <Login setLoggedIn={setLoggedIn} setRole={setRole} />} 
          />

          {/* Conditionally Render Routes Based on Login and Role */}
          {isLoggedIn ? (
            <>
              {/* User Routes */}
              {role === "User" ? (
                <Route path="/dashboard" element={<Dashboard />} />
              ) : null}

              {/* Admin Routes */}
              {role === "Admin" ? (
                <>
                  <Route path="/dashboard" element={<Dashboard />} />  {/* Ensure admins can access dashboard too */}
                  <Route path="/requests" element={<AdminTaskList />} />
                  <Route path="/stocks" element={<ITStock />} />
                  <Route path="/jobs" element={<Jobs />} />
                </>
              ) : null}

              {/* Fallback route for logged-in users trying to access undefined routes */}
              <Route path="*" element={<Navigate to="/dashboard" />} />
            </>
          ) : (
            <>
              {/* Redirect to login if not logged in */}
              <Route path="*" element={<Navigate to="/login" />} />
            </>
          )}
        </Routes>
      </Router>
    </div>
  );
}

export default App;
