import React from "react";
import { Card, Button, Row, Col } from "antd";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();

  const goToFeature1 = () => {
    navigate("/feature1");  // Replace with the actual route
  };

  const goToFeature2 = () => {
    navigate("/feature2");  // Replace with the actual route
  };

  const goToFeature3 = () => {
    navigate("/feature3");  // Replace with the actual route
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>User Dashboard</h1>
      <Row gutter={[16, 16]} justify="center" style={{ width: "100%" }}>
        {/* Feature 1 Button */}
        <Col xs={24} sm={12} md={8}>
          <Card hoverable style={styles.card}>
            <h2>Feature 1</h2>
            <p>Description of Feature 1 goes here.</p>
            <Button type="primary" block onClick={goToFeature1}>
              Go to Feature 1
            </Button>
          </Card>
        </Col>

        {/* Feature 2 Button */}
        <Col xs={24} sm={12} md={8}>
          <Card hoverable style={styles.card}>
            <h2>Feature 2</h2>
            <p>Description of Feature 2 goes here.</p>
            <Button type="primary" block onClick={goToFeature2}>
              Go to Feature 2
            </Button>
          </Card>
        </Col>

        {/* Feature 3 Button */}
        <Col xs={24} sm={12} md={8}>
          <Card hoverable style={styles.card}>
            <h2>Feature 3</h2>
            <p>Description of Feature 3 goes here.</p>
            <Button type="primary" block onClick={goToFeature3}>
              Go to Feature 3
            </Button>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: "100vh",
    padding: "20px",
    backgroundColor: "#f0f2f5",
  },
  title: {
    marginBottom: "30px",
    fontSize: "32px",
    fontWeight: "bold",
  },
  card: {
    padding: "20px",
    textAlign: "center",
    borderRadius: "8px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
  },
};

export default Dashboard;
