// Dashboard.js
import React, { useState, useEffect } from 'react';
import { Layout, Table, Card, Button, Row, Col, message } from 'antd';
import ScheduleRepair from './ScheduleRepair'; // Import the ScheduleRepair component
import axios from 'axios';

const { Content } = Layout;

const Dashboard = () => {
  const [dataSource, setDataSource] = useState([]);

  // Function to fetch scheduled repairs
  const fetchScheduledRepairs = async () => {
    try {
      const account = JSON.parse(localStorage.getItem('account') || '{}');
      if (!account || !account.token) {
        message.error('Authorization token is missing. Please log in.');
        return;
      }

      const response = await axios.get(
        'http://localhost:5235/api/ScheduledRepairs/myrepairs',
        {
          headers: {
            Authorization: `Bearer ${account.token}`,
          },
        }
      );

      const repairs = response.data;

      // Map repairs to match table data format
      const formattedRepairs = repairs.map((repair) => ({
        key: repair.id,
        title: repair.title,
        description: repair.description,
        repairDate: new Date(repair.repairDate).toLocaleString(),
        status: repair.status,
      }));

      setDataSource(formattedRepairs);
    } catch (error) {
      console.error('Error fetching scheduled repairs:', error);
      message.error('Failed to fetch scheduled repairs.');
    }
  };

  // Fetch data when component mounts
  useEffect(() => {
    fetchScheduledRepairs();
  }, []);

  // Table columns
  const columns = [
    { title: 'Title', dataIndex: 'title', key: 'title' },
    { title: 'Description', dataIndex: 'description', key: 'description' },
    { title: 'Repair Date', dataIndex: 'repairDate', key: 'repairDate' },
    { title: 'Status', dataIndex: 'status', key: 'status' },
  ];

  return (
    <Layout style={{ backgroundColor: '#f0f2f5', minHeight: '100vh' }}>
      {/* Content */}
      <Content style={{ padding: '20px' }}>
        <h1 style={{ textAlign: 'center', marginBottom: '30px' }}>Appointments</h1>

        {/* Cards */}
        <Row
          gutter={16}
          justify="center"
          align="middle"
          style={{ marginBottom: '20px', textAlign: 'center' }}
        >
          <Col xs={24} sm={12} md={8}>
            <Card
              hoverable
              cover={
                <img
                  alt="Schedule Repair Appointment"
                  src="https://cdn.pixabay.com/photo/2017/01/31/13/14/calendar-2027131_960_720.png"
                  style={{ objectFit: 'cover', height: '150px' }}
                />
              }
            >
              <Card.Meta
                title="Schedule Repair Appointment"
                description={
                  <ScheduleRepair fetchScheduledRepairs={fetchScheduledRepairs} />
                }
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={8}>
            <Card
              hoverable
              cover={
                <img
                  alt="Submit a Request"
                  src="https://cdn.pixabay.com/photo/2017/02/01/10/22/gear-2030396_960_720.png"
                  style={{ objectFit: 'cover', height: '150px' }}
                />
              }
            >
              <Card.Meta
                title="Submit a Request"
                description={
                  <Button type="primary" style={{ marginTop: '10px' }}>
                    Submit Now
                  </Button>
                }
              />
            </Card>
          </Col>
        </Row>

        {/* Table */}
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Your Appointments</h2>
        <Table dataSource={dataSource} columns={columns} pagination={false} />
      </Content>
    </Layout>
  );
};

export default Dashboard;
