import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Navbar() {
  const [account, setAccount] = useState();
  const [role, setRole] = useState();
  const navigate = useNavigate();

  const logout = async () => {
    localStorage.clear();
    navigate("/login");
    window.location.reload();
  };

  useEffect(() => {
    const account = localStorage.getItem("account");
    setRole(localStorage.getItem("role"));
    setAccount(JSON.parse(account));
  }, []);

  return (
    <nav
      style={{
        padding: "10px",
        backgroundColor: "#333",
        color: "#fff",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
      }}
    >
      <h2>Employment Hub</h2>

      <div style={{ display: "flex", gap: 20 }}>
        <Link to="/" style={{ margin: "0 10px", color: "#fff" }}>
          Home
        </Link>

        {role === "Admin" ? (
          <>
            <Link to="/requests" style={{ margin: "0 10px", color: "#fff" }}>
              Requests
            </Link>
            <Link to="/stocks" style={{ margin: "0 10px", color: "#fff" }}>
              IT Stocks
            </Link>
            <Link to="/jobs" style={{ margin: "0 10px", color: "#fff" }}>
              Jobs
            </Link>
          </>
        ) : null}
        {role === "User" ? (
          <Link to="/add-task" style={{ margin: "0 10px", color: "#fff" }}>
            Add Task
          </Link>
        ) : null}

        {account ? (
          <Link
            to="/login"
            onClick={logout}
            style={{ margin: "0 10px", color: "#fff" }}
          >
            Logout
          </Link>
        ) : null}

        {account ? (
          <p style={{ margin: "0 10px", color: "#fff" }}>
            Logged as: {account.username}
          </p>
        ) : (
          <Link to="/login" style={{ margin: "0 10px", color: "#fff" }}>
            Login
          </Link>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
