import React, { useState, useEffect } from 'react';
import {
  Layout,
  Menu,
  Table,
  Card,
  Button,
  Row,
  Col,
  message,
  Avatar,
  Badge,
  Typography,
} from 'antd';
import {
  CalendarOutlined,
  FormOutlined,
  UserOutlined,
  LogoutOutlined,
  SettingOutlined,
} from '@ant-design/icons';
import ScheduleRepair from './ScheduleRepair';
import axios from 'axios';

const { Header, Content, Footer } = Layout;
const { Title } = Typography;

const Dashboard = () => {
  const [dataSource, setDataSource] = useState([]);

  // Function to fetch scheduled repairs
  const fetchScheduledRepairs = async () => {
    try {
      const account = JSON.parse(localStorage.getItem('account') || '{}');
      if (!account || !account.token) {
        message.error('Authorization token is missing. Please log in.');
        return;
      }

      const response = await axios.get(
        'http://localhost:5235/api/ScheduledRepairs/myrepairs',
        {
          headers: {
            Authorization: `Bearer ${account.token}`,
          },
        }
      );

      const repairs = response.data;

      // Map repairs to match table data format
      const formattedRepairs = repairs.map((repair) => ({
        key: repair.id,
        title: repair.title,
        description: repair.description,
        repairDate: new Date(repair.repairDate).toLocaleString(),
        status: repair.status,
      }));

      setDataSource(formattedRepairs);
    } catch (error) {
      console.error('Error fetching scheduled repairs:', error);
      message.error('Failed to fetch scheduled repairs.');
    }
  };

  // Fetch data when component mounts
  useEffect(() => {
    fetchScheduledRepairs();
  }, []);

  // Table columns
  const columns = [
    {
      title: 'Title',
      dataIndex: 'title',
      key: 'title',
      render: (text) => <strong>{text}</strong>,
    },
    {
      title: 'Description',
      dataIndex: 'description',
      key: 'description',
      ellipsis: true,
    },
    { title: 'Repair Date', dataIndex: 'repairDate', key: 'repairDate' },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status) => {
        let color = 'processing';
        if (status === 'Pending') {
          color = 'warning';
        } else if (status === 'In Progress') {
          color = 'processing';
        } else if (status === 'Completed') {
          color = 'success';
        }
        return <Badge status={color} text={status} />;
      },
    },
  ];

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('account');
    window.location.href = '/login'; // Redirect to login page
  };

  return (
    <Layout style={{ minHeight: '100vh' }}>
      {/* Header */}
      <Header style={{ position: 'fixed', zIndex: 1, width: '100%' }}>
        <div
          style={{
            float: 'left',
            color: '#fff',
            fontSize: '20px',
            fontWeight: 'bold',
            marginRight: '20px',
          }}
        >
          <CalendarOutlined style={{ marginRight: '8px' }} />
          MyApp
        </div>
        <Menu theme="dark" mode="horizontal" style={{ lineHeight: '64px' }}>
          <Menu.Item key="1" icon={<UserOutlined />}>
            Profile
          </Menu.Item>
          <Menu.Item key="2" icon={<SettingOutlined />}>
            Settings
          </Menu.Item>
          <Menu.Item key="3" icon={<LogoutOutlined />} onClick={handleLogout}>
            Logout
          </Menu.Item>
        </Menu>
      </Header>

      {/* Content */}
      <Content
        style={{ padding: '80px 50px 50px', backgroundColor: '#f0f2f5' }}
      >
        <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
          <Title level={2} style={{ textAlign: 'center', marginBottom: '40px' }}>
            Welcome to Your Dashboard
          </Title>

          {/* Cards */}
          <Row gutter={[24, 24]} style={{ marginBottom: '40px' }}>
            <Col xs={24} sm={12} md={8}>
              <Card
                hoverable
                cover={
                  <img
                    alt="Schedule Repair Appointment"
                    src="https://cdn.pixabay.com/photo/2016/11/29/06/15/desk-1867761_960_720.jpg"
                    style={{ objectFit: 'cover', height: '200px' }}
                  />
                }
              >
                <Card.Meta
                  title="Schedule Repair Appointment"
                  description={
                    <ScheduleRepair fetchScheduledRepairs={fetchScheduledRepairs} />
                  }
                />
              </Card>
            </Col>
            <Col xs={24} sm={12} md={8}>
              <Card
                hoverable
                cover={
                  <img
                    alt="Submit a Request"
                    src="https://cdn.pixabay.com/photo/2017/02/01/10/22/gear-2030396_960_720.png"
                    style={{ objectFit: 'cover', height: '200px' }}
                  />
                }
              >
                <Card.Meta
                  title="Submit a Request"
                  description={
                    <Button type="primary" style={{ marginTop: '10px' }}>
                      Submit Now
                    </Button>
                  }
                />
              </Card>
            </Col>
            <Col xs={24} sm={12} md={8}>
              <Card
                hoverable
                cover={
                  <img
                    alt="View Profile"
                    src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
                    style={{ objectFit: 'cover', height: '200px' }}
                  />
                }
              >
                <Card.Meta
                  title="View Profile"
                  description={
                    <Button type="primary" style={{ marginTop: '10px' }}>
                      View Now
                    </Button>
                  }
                />
              </Card>
            </Col>
          </Row>

          {/* Table */}
          <Title level={3} style={{ marginBottom: '20px' }}>
            Your Appointments
          </Title>
          <Table
            dataSource={dataSource}
            columns={columns}
            pagination={{ pageSize: 5 }}
            style={{ backgroundColor: '#fff', padding: '20px' }}
          />
        </div>
      </Content>

      {/* Footer */}
      <Footer style={{ textAlign: 'center' }}>
        MyApp ©2023 Created by Your Company
      </Footer>
    </Layout>
  );
};

export default Dashboard;
