import React from 'react';
import { Layout, Table, Card, Button, Row, Col } from 'antd';
import ScheduleRepair from './ScheduleRepair'; // Import the ScheduleRepair component

const { Content } = Layout;

const Dashboard = () => {
  // Hardcoded data for the table
  const dataSource = [
    {
      key: '1',
      customerName: 'Ahmad',
      issue: 'Computer Screen',
      startDate: '9/4/2024',
      deadline: '1 week',
      location: 'Khalda Amman sunshine company',
      technician: 'Rafi Barudak',
    },
    {
      key: '2',
      customerName: 'Ahmad',
      issue: 'Computer Screen',
      startDate: '9/4/2024',
      deadline: '1 week',
      location: 'Khalda Amman sunshine company',
      technician: 'Rafi Barudak',
    },
    {
      key: '3',
      customerName: 'Ahmad',
      issue: 'Computer Screen',
      startDate: '9/4/2024',
      deadline: '1 week',
      location: 'Khalda Amman sunshine company',
      technician: 'Rafi Barudak',
    },
  ];

  // Table columns
  const columns = [
    { title: 'Customer Name', dataIndex: 'customerName', key: 'customerName' },
    { title: 'Issues', dataIndex: 'issue', key: 'issue' },
    { title: 'Start Date', dataIndex: 'startDate', key: 'startDate' },
    { title: 'Deadline', dataIndex: 'deadline', key: 'deadline' },
    { title: 'Location', dataIndex: 'location', key: 'location' },
    { title: 'Technician', dataIndex: 'technician', key: 'technician' },
  ];

  return (
    <Layout style={{ backgroundColor: '#f0f2f5', minHeight: '100vh' }}>
      {/* Content */}
      <Content style={{ padding: '20px' }}>
        <h1 style={{ textAlign: 'center', marginBottom: '30px' }}>Customer Dashboard</h1>

        {/* Cards */}
        <Row
          gutter={16}
          justify="center"
          align="middle"
          style={{ marginBottom: '20px', textAlign: 'center' }}
        >
          <Col span={8}>
            <Card
              hoverable
              cover={
                <img
                  alt="Schedule Repair Appointment"
                  src="https://cdn.pixabay.com/photo/2016/03/27/07/08/clock-1281608_960_720.jpg"
                  style={{ objectFit: 'cover', height: '150px' }}
                />
              }
            >
              <Card.Meta
                title="Schedule Repair Appointment"
                description={<ScheduleRepair />} // Use the ScheduleRepair component here
              />
            </Card>
          </Col>
          <Col span={8}>
            <Card
              hoverable
              cover={
                <img
                  alt="Submit a request"
                  src="https://cdn.pixabay.com/photo/2016/04/19/13/39/checklist-1331910_960_720.jpg"
                  style={{ objectFit: 'cover', height: '150px' }}
                />
              }
            >
              <Card.Meta
                title="Submit a request"
                description={
                  <Button type="primary" style={{ marginTop: '10px' }}>
                    Submit Now
                  </Button>
                }
              />
            </Card>
          </Col>
        </Row>

        {/* Table */}
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>History</h2>
        <Table dataSource={dataSource} columns={columns} pagination={false} />
      </Content>
    </Layout>
  );
};

export default Dashboard;
