import "./App.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Login from "./Components/login";
import { useEffect, useState } from "react";
import Navbar from "./Components/Navbar";
import TaskForm from "./Components/TaskForm";
import AdminTaskList from "./Components/AdminTaskList";
import ITStock from "./Components/ITStock";
import Jobs from "./Components/Jobs";
import React from 'react';


function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [role, setRole] = useState();

  useEffect(() => {
    setLoggedIn(localStorage.getItem("account"));
    setRole(localStorage.getItem("role"));
  }, []);

  return (
    <div>
      <Router>
        <Navbar />
        <Routes>
          <Route
            path="/"
            element={
              <div
                style={{
                  backgroundImage:
                    "url(https://c0.wallpaperflare.com/preview/751/2/550/chart-graph-business-finance.jpg)",
                  backgroundSize: "cover",
                  backgroundPosition: "center",
                  backgroundRepeat: "no-repeat",
                  height: "91vh",
                }}
              ></div>
            }
          />

          {isLoggedIn ? (
            <>
              {role === "User" ? (
                <Route path="/add-task" element={<TaskForm />} />
              ) : null}
              {role === "Admin" ? (
                <>
                  <Route path="/requests" element={<AdminTaskList />} />
                  <Route path="/stocks" element={<ITStock />} />
                  <Route path="/jobs" element={<Jobs />} />
                </>
              ) : null}
            </>
          ) : (
            <>
              <Route path="/login" element={<Login />} />
            </>
          )}
        </Routes>
      </Router>
    </div>
  );
}

export default App;
