import "./App.css";
import { BrowserRouter as Router, Route, Routes, Navigate, useNavigate } from "react-router-dom";  // Import Navigate and useNavigate
import Login from "./Components/login";
import Register from "./Components/Register";  // Import the Register component
import { useEffect, useState } from "react";
import Navbar from "./Components/Navbar";
import TaskForm from "./Components/TaskForm";
import AdminTaskList from "./Components/AdminTaskList";
import ITStock from "./Components/ITStock";
import Jobs from "./Components/Jobs";
import React from 'react';

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [role, setRole] = useState(null);
  const navigate = useNavigate();  // useNavigate hook for redirection

  useEffect(() => {
    const account = localStorage.getItem("account");
    const userRole = localStorage.getItem("role");

    console.log("Account from localStorage:", account);
    console.log("Role from localStorage:", userRole);

    // Set login state based on existence of account
    setLoggedIn(!!account);
    setRole(userRole);

    // If the user is logged in, redirect based on their role
    if (account && userRole) {
      if (userRole === "Admin") {
        console.log("Redirecting to /requests");
        navigate("/requests");  // Redirect Admin to the requests page
      } else if (userRole === "User") {
        console.log("Redirecting to /add-task");
        navigate("/add-task");  // Redirect User to the task submission page
      }
    }
  }, [navigate]);  // Adding navigate as a dependency to ensure redirection works

  if (isLoggedIn && !role) {
    return <div>Loading...</div>;  // Safeguard while role is being fetched
  }

  return (
    <div>
      <Router>
        <Navbar />
        <Routes>
          {/* Public Route */}
          <Route
            path="/"
            element={
              <div
                style={{
                  backgroundImage:
                    "url(https://c0.wallpaperflare.com/preview/751/2/550/chart-graph-business-finance.jpg)",
                  backgroundSize: "cover",
                  backgroundPosition: "center",
                  backgroundRepeat: "no-repeat",
                  height: "91vh",
                }}
              ></div>
            }
          />

          {/* Add the Registration Route */}
          <Route path="/Register" element={<Register />} />

          {/* Conditionally Render Routes Based on Login and Role */}
          {isLoggedIn ? (
            <>
              {/* User Routes */}
              {role === "User" ? (
                <Route path="/add-task" element={<TaskForm />} />
              ) : null}

              {/* Admin Routes */}
              {role === "Admin" ? (
                <>
                  <Route path="/requests" element={<AdminTaskList />} />
                  <Route path="/stocks" element={<ITStock />} />
                  <Route path="/jobs" element={<Jobs />} />
                </>
              ) : null}

              {/* Fallback route for logged-in users trying to access undefined routes */}
              <Route path="*" element={<Navigate to="/" />} />
            </>
          ) : (
            <>
              {/* Redirect to login if not logged in */}
              <Route path="/login" element={<Login />} />
              <Route path="*" element={<Navigate to="/login" />} />
            </>
          )}
        </Routes>
      </Router>
    </div>
  );
}

export default App;
