import React, { useEffect, useState, useMemo } from "react";
import {
  Button,
  Form,
  Input,
  InputNumber,
  Modal,
  notification,
  Table,
} from "antd";
import axios from "axios";
import Swal from "sweetalert2";

const { Column } = Table;
const Context = React.createContext({
  name: "Default",
});

function ITStock() {
  const [stocks, setStocks] = useState();
  const [isModalOpen, setIsModalOpen] = useState(false);

  // start of notification
  const [api, contextHolder] = notification.useNotification();

  const openNotification = (placement) => {
    api.info({
      message: "Update Successful",
      description: (
        <Context.Consumer>
          {({ name }) => `Stock ${isModalOpen.name} Updated`}
        </Context.Consumer>
      ),
      placement,
    });
  };

  const contextValue = useMemo(
    () => ({
      name: "Ant Design",
    }),
    []
  );
  // end of notification

  const showModal = (record) => {
    setIsModalOpen(record);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const onFinishFailed = (errorInfo) => {
    handleCancel();
  };

  // step 1
  const getStocks = async () => {
    const account = JSON.parse(localStorage.getItem("account"));

    try {
      const response = await axios.get(
        "https://localhost:7000/api/ITStocks/allStocks",
        {
          headers: { Authorization: `Bearer ${account.token}` },
        }
      );

      setStocks(response.data);
    } catch (e) {
      Swal.fire({
        icon: "error",
        title: "Oops...",
        text: "Something went wrong!",
        color: "red",
        width: 400,
      });
    }
  };

  // step 5
  const onFormFinish = async (values) => {
    const account = JSON.parse(localStorage.getItem("account"));
    try {
      await axios.put(
        `https://localhost:7000/api/ITStocks/${isModalOpen.id}`,
        {
          quantityInStock:
            values.quantityInStock || isModalOpen.quantityInStock,
          description: values.description || isModalOpen.description,
          category: values.category || isModalOpen.category,
          name: values.name || isModalOpen.name,
        },
        {
          headers: {
            Authorization: `Bearer ${account.token}`,
            "Content-Type": "application/json",
          },
        }
      );

      getStocks();
      handleCancel();
      openNotification("bottomRight");
    } catch (e) {
      Swal.fire({
        icon: "error",
        title: "Oops...",
        text: "Something went wrong!",
        color: "red",
        width: 400,
      });
    }
  };

  // step 7
  const onSearch = async (value) => {
    const account = JSON.parse(localStorage.getItem("account"));
    try {
      const response = await axios.get(
        `https://localhost:7000/api/ITStocks/search?name=${value}`,
        {
          headers: { Authorization: `Bearer ${account.token}` },
        }
      );

      setStocks(response.data);
    } catch (e) {
      Swal.fire({
        icon: "error",
        title: "Oops...",
        text: "Item doesn't exist!",
        color: "red",
        width: 400,
      });
      getStocks();
    }
  };

  useEffect(() => {
    getStocks();
  }, []);

  return (
    <Context.Provider value={contextValue}>
      {contextHolder}

      <div
        style={{ padding: "20px 0", display: "flex", justifyContent: "center" }}
      >
        {/*step 6*/}
        <Input.Search
          style={{ width: "33%" }}
          placeholder="Search"
          // onSearch={onSearch}
          onChange={(e) => onSearch(e.target.value)}
        />
      </div>

      {/*step 2*/}
      <div style={{ paddingTop: 20 }}>
        2
        <Table
          dataSource={stocks}
          bordered
          style={{ width: "90%", margin: "0 auto" }}
        >
          <Column title="Name" dataIndex="name" key="name" />
          <Column title="Category" dataIndex="category" key="category" />
          <Column
            title="Description"
            dataIndex="description"
            key="description"
          />
          <Column
            title="Quantity In Stock"
            dataIndex="quantityInStock"
            key="quantityInStock"
            align="center"
          />
          <Column
            title="Action"
            key="action"
            align="center"
            // step 4
            render={(_, record) => (
              <Button type="default" onClick={() => showModal(record)}>
                Edit
              </Button>
            )}
          />
        </Table>
        {/*step 3*/}
        <Modal
          title="Edit Stock"
          open={isModalOpen}
          onCancel={handleCancel}
          footer={[<></>]}
        >
          <Form
            id="submitRequestForm"
            name="basic"
            layout="vertical"
            autoComplete="off"
            onFinish={onFormFinish}
            onFinishFailed={onFinishFailed}
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              marginTop: 30,
            }}
          >
            <Form.Item label="Name" name="name">
              <Input defaultValue={isModalOpen.name} disabled />
            </Form.Item>

            <Form.Item label="Category" name="category">
              <Input defaultValue={isModalOpen.category} disabled />
            </Form.Item>

            <Form.Item label="Description" name="description">
              <Input.TextArea defaultValue={isModalOpen.description} />
            </Form.Item>

            <Form.Item label="Quantity In Stock" name="quantityInStock">
              <InputNumber defaultValue={isModalOpen.quantityInStock} />
            </Form.Item>

            <Form.Item style={{ alignSelf: "center" }}>
              <Button type="primary" htmlType="submit">
                Submit
              </Button>
            </Form.Item>
          </Form>
        </Modal>
      </div>
    </Context.Provider>
  );
}

export default ITStock;
