import React, { useEffect } from "react";
import { Button } from "antd";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();

  // Debugging
  useEffect(() => {
    console.log("Dashboard component loaded");
  }, []);

  const handleButtonClick = (path) => {
    console.log("Navigating to:", path);
    navigate(path);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Welcome to Your Dashboard</h1>
      <div style={styles.buttonContainer}>
        <Button
          type="primary"
          size="large"
          onClick={() => handleButtonClick("/add-task")}
          style={styles.button}
        >
          Add Task
        </Button>
        <Button
          type="primary"
          size="large"
          onClick={() => handleButtonClick("/requests")}
          style={styles.button}
        >
          View Requests
        </Button>
        <Button
          type="primary"
          size="large"
          onClick={() => handleButtonClick("/jobs")}
          style={styles.button}
        >
          View Jobs
        </Button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    background: "radial-gradient(circle, rgba(255,255,255,1) 0%, rgba(230,230,230,1) 100%)",
    border: "2px solid red", // Add a border to see if the container is being rendered
  },
  title: {
    fontSize: "36px",
    fontWeight: "bold",
    marginBottom: "40px",
    textAlign: "center",
  },
  buttonContainer: {
    display: "flex",
    flexDirection: "row",
    gap: "20px",
    border: "1px solid blue", // Add a border to see if buttons container is rendering
  },
  button: {
    width: "150px",
    height: "50px",
    backgroundColor: "#f0f0f0", // Add a background color to ensure visibility
  },
};

export default Dashboard;
