import "./App.css";
import { BrowserRouter as Router, Route, Routes, Navigate, useNavigate } from "react-router-dom";  // Import useNavigate
import Login from "./Components/login";
import Register from "./Components/Register";  // Import the Register component
import { useEffect, useState } from "react";
import Navbar from "./Components/Navbar";
import TaskForm from "./Components/TaskForm";
import AdminTaskList from "./Components/AdminTaskList";
import ITStock from "./Components/ITStock";
import Jobs from "./Components/Jobs";
import React from 'react';

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [role, setRole] = useState();
  const navigate = useNavigate();  // Use the useNavigate hook for redirection

  useEffect(() => {
    const account = localStorage.getItem("account");
    const userRole = localStorage.getItem("role");

    setLoggedIn(!!account);  // Set to true if account exists
    setRole(userRole);

    // Automatically redirect based on role after login
    if (account && userRole) {
      if (userRole === "User") {
        navigate("/add-task");  // Redirect to the User's task form
      } else if (userRole === "Admin") {
        navigate("/requests");  // Redirect to the Admin's request list
      }
    }
  }, [navigate]);

  return (
    <div>
      <Router>
        <Navbar />
        <Routes>
          {/* Public Route */}
          <Route
            path="/"
            element={
              <div
                style={{
                  backgroundImage:
                    "url(https://c0.wallpaperflare.com/preview/751/2/550/chart-graph-business-finance.jpg)",
                  backgroundSize: "cover",
                  backgroundPosition: "center",
                  backgroundRepeat: "no-repeat",
                  height: "91vh",
                }}
              ></div>
            }
          />

          {/* Add the Registration Route */}
          <Route path="/Register" element={<Register />} />

          {/* Conditionally Render Routes Based on Login and Role */}
          {isLoggedIn ? (
            <>
              {/* User Routes */}
              {role === "User" ? (
                <Route path="/add-task" element={<TaskForm />} />
              ) : null}

              {/* Admin Routes */}
              {role === "Admin" ? (
                <>
                  <Route path="/requests" element={<AdminTaskList />} />
                  <Route path="/stocks" element={<ITStock />} />
                  <Route path="/jobs" element={<Jobs />} />
                </>
              ) : null}

              {/* Fallback route for logged-in users trying to access undefined routes */}
              <Route path="*" element={<Navigate to="/" />} />
            </>
          ) : (
            <>
              {/* Redirect to login if not logged in */}
              <Route path="/login" element={<Login />} />
              <Route path="*" element={<Navigate to="/login" />} />
            </>
          )}
        </Routes>
      </Router>
    </div>
  );
}

export default App;
